sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {

    const {
        searchContextConfigId,
        searchTerm
    } = event.payload;

    api.emit('NAV_ITEM_SELECTED', {
        route: "search",
        fields: {
            searchContextConfigId
        },
        params: {
            searchTerm
        }
    });
}
